INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (1, 'Healthcare & Diagnostics', 'healthcare', 'Precision labeling for blood collection tubes, diagnostic kits, specimen containers, and medical devices. Our labels withstand sterilization, refrigeration, and chemical exposure while maintaining barcode scannability.', '/uploads/6e5b799b-88a8-45b5-9748-cc48a9a7ab7b.jpg', '', 'Blood vial & specimen labels,Autoclave-safe sterilization labels,Medical device tracking barcodes,Patient identification wristbands', true, 1, '2026-01-03 16:58:16.778001', '2026-02-23 13:02:15.89124') ON CONFLICT (id) DO NOTHING;
INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (2, 'Pharmaceuticals', 'pharmaceuticals', 'Compliant pharmaceutical labeling for drug packaging, tablet bottles, syringes, and clinical trial products. Our labels meet FDA and regulatory standards with tamper-evident and serialization features.', '/uploads/d9943758-26b6-4399-9f4a-370a95e2bb5c.jpg', '', 'Drug bottle & carton labels,Tamper-evident security seals,Serialization & track-and-trace,Clinical trial labeling', true, 2, '2026-01-03 16:58:16.8119616', '2026-02-23 13:02:15.9058137') ON CONFLICT (id) DO NOTHING;
INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (3, 'Chemical Industry', 'chemicals', 'GHS-compliant chemical labeling for drums, containers, and hazardous material packaging. Our labels resist chemical exposure, solvents, and extreme temperatures for safe identification.', '/uploads/101ef9a9-5b2c-4662-8b08-8be14c54c702.jpg', '', 'GHS hazard warning labels,Chemical-resistant materials,Drum & container labeling,Safety data sheet labels', true, 5, '2026-01-03 16:58:16.8277307', '2026-02-23 13:02:15.9215323') ON CONFLICT (id) DO NOTHING;
INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (4, 'Automobile', 'automobile', 'Durable VIN labels, under-hood tracking stickers, and parts identification for the automotive industry. Our labels withstand extreme heat, oil, and UV exposure in engine compartments.', '/uploads/74a7989e-ae61-4b03-ad79-aafe28244bdb.jpg', '', 'VIN & chassis number labels,Under-hood heat resistant,Parts tracking barcodes,Tire & component stickers', true, 7, '2026-01-03 16:58:16.8428348', '2026-02-23 13:02:15.9317742') ON CONFLICT (id) DO NOTHING;
INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (5, 'Engineering & Manufacturing', 'manufacturing', 'Industrial-grade labels for machinery, equipment, and manufactured goods. Designed for asset tracking, quality control, calibration stickers, and warehouse inventory management.', '/uploads/ee1c4aee-e520-4a44-a343-6ae8d2624055.jpg', '', 'Asset tracking labels,Quality control stickers,Calibration & inspection tags,Warehouse inventory labels', true, 8, '2026-01-03 16:58:16.8578436', '2026-02-23 13:02:15.9374037') ON CONFLICT (id) DO NOTHING;
