INSERT INTO industries (id, name, slug, description, image_url, icon, features, is_active, display_order, created_at, updated_at) VALUES (7, 'Courier & Logistics', 'logistics', 'High-volume shipping labels, AWB stickers, and logistics barcodes for courier and warehousing operations. Designed for fast printing on thermal printers with excellent scan rates.', '/uploads/58468253-78d6-4bb2-92c9-d765be593c9b.jpg', '', 'Shipping & AWB labels,Warehouse rack labels,Route & sorting barcodes,E-commerce packing slips', true, 10, '2026-01-03 16:58:16.8896875', '2026-02-23 13:02:15.9472755') ON CONFLICT (id) DO NOTHING;
