INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (1, 'What types of labels do you manufacture?', 'We manufacture a wide range of labels including barcode labels, product labels, packaging labels, security labels, promotional labels, industrial labels, and custom-designed labels for various applications.', 'Products', true, 1, '2026-01-03 16:57:27.4405992', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (2, 'What is the minimum order quantity?', 'Our minimum order quantity varies depending on the label type and specifications. For standard labels, we typically accept orders starting from 1,000 pieces. For custom designs, please contact us for specific requirements.', 'Orders', true, 2, '2026-01-03 16:57:27.4693463', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (3, 'What materials do you use for labels?', 'We work with various materials including paper, vinyl, polyester, polypropylene, and specialty materials. Each material is selected based on the intended application, durability requirements, and environmental conditions.', 'Products', true, 3, '2026-01-03 16:57:27.4847537', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (4, 'Do you offer custom design services?', 'Yes, our in-house design team can help create custom label designs tailored to your brand. We offer complete design services from concept to final production, ensuring your labels meet both aesthetic and functional requirements.', 'Services', true, 4, '2026-01-03 16:57:27.4997644', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (5, 'What is your typical production turnaround time?', 'Standard orders are typically completed within 5-7 business days. Rush orders can be accommodated with expedited production, usually within 2-3 business days, subject to order complexity and current production schedule.', 'Orders', true, 5, '2026-01-03 16:57:27.5140348', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (6, 'Do you provide samples before bulk orders?', 'Yes, we provide sample labels for approval before proceeding with bulk production. This ensures you are completely satisfied with the quality, design, and specifications before committing to a larger order.', 'Orders', true, 6, '2026-01-03 16:57:27.5286042', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (7, 'What printing technologies do you use?', 'We utilize state-of-the-art printing technologies including flexographic printing, digital printing, and thermal transfer printing. This allows us to deliver high-quality labels with excellent color accuracy and durability.', 'Products', true, 7, '2026-01-03 16:57:27.5433685', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (8, 'Do you ship nationwide?', 'Yes, we provide shipping services across India. We work with reliable logistics partners to ensure safe and timely delivery of your orders. International shipping can also be arranged upon request.', 'Shipping', true, 8, '2026-01-03 16:57:27.5589315', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (17, 'What standard label sizes are available?', 'We offer labels in four size categories: Small (25x12mm, 25x25mm, 38x25mm) for jewellery, MRP tags, and vials; Medium (50x25mm, 50x38mm, 75x38mm) for product labeling, retail, and barcodes; Large (100x50mm, 100x75mm, 100x100mm) for carton boxes, shipping, and GS1 barcodes; and Extra-Large (100x150mm, 150x100mm, A4/A5 sheets) for logistics, e-commerce shipping, and full-page labels. Custom sizes are also available on request.', 'Products', true, 9, '2026-02-23 12:40:22.2949443', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (18, 'What are Taffeta labels and where are they used?', 'Taffeta labels are soft, woven fabric labels primarily used in the textile and garment industry for wash-care instructions, brand labels, size tags, and fabric composition details. They are comfortable against skin, withstand multiple wash cycles, and can be printed using wash-care ribbons. Taffeta rolls are available in white and ivory, and can be printed with care symbols, text, and barcodes using standard thermal transfer printers.', 'Products', true, 13, '2026-02-23 12:40:23.2893023', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (19, 'Which industries do you serve?', 'We serve 13+ industries including FMCG, Food & Beverage, Pharmaceuticals, Cosmetics & Personal Care, Logistics & Warehousing, Engineering & Manufacturing, Textile & Garments, Electronics & IT, Automobile, Chemical Industry, Agriculture & Horticulture, Healthcare & Diagnostics, and Jewellery & Luxury Retail. Each industry has unique labeling requirements and we provide customized solutions to meet specific regulatory and application needs.', 'General', true, 14, '2026-02-23 12:40:23.538178', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (20, 'What is UPS (Ups) logic in label printing?', 'UPS refers to the number of labels printed side-by-side across the width of a roll. 1-Up means a single label per row (standard for most applications). 2-Up places two labels side by side, doubling output per roll, ideal for high-volume production. 3-Up has three labels across, used in industrial and logistics settings. More ups per row means faster printing and lower per-label cost, but requires compatible printer width.', 'Technical', true, 10, '2026-02-23 12:40:38.3544439', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (21, 'What core diameters are available for label and ribbon rolls?', 'We offer three standard core diameters: 1 inch (25.4mm), the most common size compatible with desktop printers like Zebra, TSC, and Honeywell; 2 inch (50.8mm), used in semi-industrial and mid-range printers for higher roll capacity; and 3 inch (76.2mm), designed for high-speed industrial printers requiring maximum roll length and continuous printing. The right core size depends on your printer model and production volume.', 'Technical', true, 11, '2026-02-23 12:40:38.4462128', NULL) ON CONFLICT (id) DO NOTHING;
INSERT INTO faqs (id, question, answer, category, is_active, display_order, created_at, updated_at) VALUES (22, 'What is the difference between Wax, Wax-Resin, and Resin ribbons?', 'Wax ribbons are the most economical, best for paper-based labels in general-purpose applications like shipping and inventory. Wax-Resin ribbons offer enhanced durability with better scratch and smudge resistance, suitable for semi-gloss and synthetic labels used in retail and compliance. Resin ribbons provide maximum durability with resistance to chemicals, solvents, heat, and abrasion, essential for harsh industrial environments, outdoor use, and synthetic labels. We also offer Premium Wax-Resin and Wash-Care ribbons for specialized industries.', 'Technical', true, 12, '2026-02-23 12:40:38.5301049', NULL) ON CONFLICT (id) DO NOTHING;
